import React, { Component } from "react";
import ProductList from "./ProductList";
import ProductDetail from "./ProductDetail";
import ModalCart from "./ModalCart";
class ShoppingCart extends Component {
  prodList = [
    {
      maSP: 1,
      tenSP: "VinSmart Live",
      manHinh: 'AMOLED, 6.2", Full HD+',
      heDieuHanh: "Android 9.0 (Pie)",
      cameraTruoc: "20 MP",
      cameraSau: "Chính 48 MP & Phụ 8 MP, 5 MP",
      ram: "4 GB",
      rom: "64 GB",
      giaBan: 5700000,
      hinhAnh: "./img/vsphone.jpg",
    },

    {
      maSP: 2,
      tenSP: "Meizu 16Xs",
      manHinh: "AMOLED, FHD+ 2232 x 1080 pixels",
      heDieuHanh: "Android 9.0 (Pie); Flyme",
      cameraTruoc: "20 MP",
      cameraSau: "Chính 48 MP & Phụ 8 MP, 5 MP",
      ram: "4 GB",
      rom: "64 GB",
      giaBan: 7600000,
      hinhAnh: "./img/meizuphone.jpg",
    },

    {
      maSP: 3,
      tenSP: "Iphone XS Max",
      manHinh: 'OLED, 6.5", 1242 x 2688 Pixels',
      heDieuHanh: "iOS 12",
      cameraSau: "Chính 12 MP & Phụ 12 MP",
      cameraTruoc: "7 MP",
      ram: "4 GB",
      rom: "64 GB",
      giaBan: 27000000,
      hinhAnh: "./img/applephone.jpg",
    },
  ];
  state = {
    prodDetail: {
      maSP: 1,
      tenSP: "VinSmart Live",
      manHinh: 'AMOLED, 6.2", Full HD+',
      heDieuHanh: "Android 9.0 (Pie)",
      cameraTruoc: "20 MP",
      cameraSau: "Chính 48 MP & Phụ 8 MP, 5 MP",
      ram: "4 GB",
      rom: "64 GB",
      giaBan: 5700000,
      hinhAnh: "./img/vsphone.jpg",
    },
    cartList: [],
  };
  handleDeleteCart = (maSP) => {
    const { cartList } = this.state;
    const index = cartList.findIndex((cart) => cart.maSP === maSP);
    if (index !== -1) {
      cartList.splice(index, 1);
      // setState
      this.setState({
        // cartList: cartList,
        cartList,
      });
    }
  };
  handleAddCart = (prod) => {
    const { cartList } = this.state;
    const index = cartList.findIndex((cart) => cart.maSP === prod.maSP);
    let newCartList = [];
    if (index === -1) {
      // thêm mới vào mãng cart list
      // thêm phần tử mới vào
      const newCart = { ...prod, soLuong: 1 };
      newCartList = [...this.state.cartList, newCart];
    } else {
      // tăng số lượng sản phẩm
      cartList[index].soLuong += 1;
      newCartList = cartList;
    }

    // setState
    this.setState({
      cartList: newCartList,
    });
  };
  handleChangeProdDetail = (prodDetail) => {
    console.log("proDetail : ", prodDetail);
    // setState
    this.setState({
      // prodDetail: prodDetail,
      prodDetail, // es6 object literal
    });
  };
  render() {
    return (
      <section className="container">
        <h3 className="title text-center">Bài tập giỏ hàng</h3>
        <div className="container text-center my-2">
          <button className="btn btn-danger " data-toggle="modal" data-target="#modelId">
            Giỏ hàng (0)
          </button>
        </div>
        <ProductList
          handleAddCart={this.handleAddCart}
          handleChangeProdDetail={this.handleChangeProdDetail}
          prodList={this.prodList}
        />
        <ModalCart handleDeleteCart={this.handleDeleteCart} cartList={this.state.cartList} />
        <ProductDetail prodDetail={this.state.prodDetail} />
      </section>
    );
  }
}

export default ShoppingCart;
