import React, { Component } from "react";
import axios from "axios";
class Mounting extends Component {
  /**
   * @constructor
   *    - chạy khi nào ? => chạy khi chúng ta gọi component
   *    - sử dụng làm gì ? => kế thừa props , khởi tạo state , refs ( DOM trong ReactJS )
   */
  constructor(props) {
    console.log("constructor");
    super(props); // kế thừa props từ class Component
    this.state = {
      name: "Nguyễn Phong Hào",
      myClass: "fe33",
    };
    this.myInput = React.createRef(); // khởi tạo ref để dom tới các thẻ cần thiết
  }
  /**
   * @render
   *    - chạy khi nào ? => chạy sau constructor và chạy khi setState, new Props , forceUpdate()
   *    - sử dụng làm gì ? => hiển thị nội dung lên màn hình ( jsx => html => render lên index.html )
   */
  render() {
    console.log("render");
    return (
      <div>
        <h2>Mounting</h2>
        <input type="text" name="hovaten" value={this.state.name} ref={this.myInput} />
      </div>
    );
  }

  /**
   * @componentDidMount
   *    - chạy khi nào ? => sau render ,chỉ chạy trong giai đoạn mounting
   *    - sử dụng làm gì ? => call api , xử lý ref ( DOM )
   */
  componentDidMount() {
    console.log("componentDidMount");
    // call api ( lên server lấy data về )
    axios({
      method: "GET",
      url: "https://elearning0706.cybersoft.edu.vn/api/QuanLyKhoaHoc/LayDanhSachKhoaHoc?MaNhom=GP01",
    })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
    // xử lý ref ( DOM REACTJS )
    console.log("đây là thẻ input : ", this.myInput.current);
  }
}

export default Mounting;
