import React, { Component } from "react";

class Unmouting extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isTaiOrXiu: true,
    };
  }

  render() {
    return (
      <div>
        <h2>Unmouting</h2>
        <p> kết quả : {this.state.isTaiOrXiu ? "Tài" : "Xỉu"} </p>
      </div>
    );
  }
  componentDidMount() {
    this.toggleTaiXiu = setInterval(() => {
      this.setState({
        isTaiOrXiu: !this.state.isTaiOrXiu,
      });
    }, 500);
  }
  /**
   * @constructor
   *    - chạy khi nào ? => chạy khi component rời khoải cay DOM Reactjs
   *    - sử dụng làm gì ? => dọn dẹp các phương thức còn soát lại
   */
  componentWillUnmount() {
    console.log("componentWillUnmount");
    clearInterval(this.toggleTaiXiu); // dừng interval
  }
}

export default Unmouting;
